var sendmail = require('sendmail')()
var callBack = function(err, reply){

}
module.exports = {
	mailMultiple: function(to,from,subj,msg,count){
		if(count < 1)
			return;
		for(var i=1; i<=count; i++){
               			sendmail({
               			from: from,
               			to: to,
               			subject: subj,
               			content: msg
			}, callBack);
		}
       	},
	mail: function(to,from,subj,msg){
		sendmail({
                	from: from,
                	to: to,
                        subject: subj,
                        content: msg
                }, callBack);
        }
}
